import React, { Component } from 'react'
import literals from './literals'
import Results from '../Results'
import { Link } from "react-router-dom"
import logic from '../../logic';
import StopCode from '../StopCode';

class CodeSearch extends Component {
    state = { error: null, line: null, lines:[] }

    handleSearch = stop_id =>
        logic.upcomingBusesByStop(stop_id)
            .then((lines) =>
                this.setState({ line: null, lines})
            )
            .catch(error =>
                this.setState({ error: error.message })
            )


    render() {
        const {
            handleSearch,
            state: { lines, line },
            props: { lang }
        } = this

        const { back} = literals[lang]

        return <main>
            <Link to={`/`}><button>{back}</button></Link> 
            <StopCode lang={lang} onSearch={handleSearch} />
            {!line && <Results lang={lang} items={lines}/>}
        </main>
    }
}

export default CodeSearch
