import React from 'react'
import literals from './literals'
import { Route, withRouter, Redirect, Switch, Link } from 'react-router-dom'

function Favorites({ lang, items, stop, error, onFav, favs }) {

    const { title1, back, fav } = literals[lang]
    const isFav = favs.some(fav => fav.id === stop)

    return <section>
    <Link to={`/byidstop`}><button>{back}</button></Link>
        <h2>{title1}</h2>
        <ul>
            {
                items.map(({ stop_id, stop_name }) => {
                    return <li key={stop_id}>
                        <h2>{stop_id} - {stop_name}</h2>
                        <button onClick={()=>onFav(stop_id)}>{fav}</button>
                    </li>
                })
            }
        </ul>
        <h2>{error}</h2>

    </section>
}

export default Favorites
