import React from 'react'
import literals from './literals'
import { Link } from "react-router-dom"
import logic from '../../logic';

function StopLine({ lang, onSearch, error }) {
    const { title, stop, submit, reset, back } = literals[lang]

    logic.retrieveBusLines(undefined)
        .then((res => {
            return <section>
                <Link to={`/`}><button>{back}</button></Link>
                <div>
                    <h1>busca1</h1>
                    <select onChange={handleSubmit}>


                        {
                            res.map(({ line, desc_line }) => {

                                return <option key={line}>{line}-{desc_line}</option>
                            })
                        }
                    </select>
                </div>
            </section>



        }))


    function handleSubmit(e) {
        e.preventDefault()

        const {
            stop_id: { value: stop_id },
        } = e.target
        let number = parseInt(stop_id, 10);

        onSearch(number)


    }


}
export default StopLine
