import React, { Component } from 'react'
import literals from './literals'
import { Route, withRouter, Redirect, Switch, Link } from 'react-router-dom'
import logic from '../../logic';
import StopLine from '../StopLine';
import ResultsLine from '../ResultsLine';
import StopLine2 from '../StopLine2';
import StopLine3 from '../StopLine3';

class CodeSearch extends Component {
    state = { error: null, line_id: null, lines:[] , stop: null, directions:[],direction:null}

    handleLineSearch = line_id =>
        logic.upcomingBusesByStop(line_id)
            .then((directions) =>
                this.setState({ error:null, directions, line_id:line_id},() => this.props.history.push('/byidline/results'))
            )
            .catch(error =>
                this.setState({ error: error.message })
            )

    handleDirectionSearch = stop_id =>
    logic.upcomingBusesByStop(stop_id)
        .then((lines) =>
            this.setState({ error:null, line: null, lines, stop:stop_id},() => this.props.history.push('/byidline/results'))
        )
        .catch(error =>
            this.setState({ error: error.message })
        )

    handleStopSearch = stop_id =>
        logic.upcomingBusesByStop(stop_id)
            .then((lines) =>
                this.setState({ error:null, line: null, lines, stop:stop_id},() => this.props.history.push('/byidline/results'))
            )
            .catch(error =>
                this.setState({ error: error.message })
            )


    render() {
        const {
            handleLineSearch,
            handleDirectionSearch,
            handleStopSearch,
            state: { lines, error, stop },
            props: { lang }
        } = this

        const {back} = literals[lang]

        return <main>
            <Switch>
            <Route exact path="/byidline" render={() => logic.isUserLoggedIn ? <StopLine lang={lang} items={lines} onSearch={handleLineSearch} error={error}/> : <Redirect to="/" />} />
            <Route exact path="/byidline/direction" render={() => logic.isUserLoggedIn ? <StopLine2 lang={lang} items={lines} onSearch={handleDirectionSearch} error={error}/> : <Redirect to="/" />} />
            <Route exact path="/byidline/stop" render={() => logic.isUserLoggedIn ? <StopLine3 lang={lang} items={lines} onSearch={handleStopSearch} error={error}/> : <Redirect to="/" />} />
            <Route path="/byidline/results" render={() => logic.isUserLoggedIn ? <ResultsLine lang={lang} items={lines} stop={stop} error={error}/> : <Redirect to="/" />} />
            <Redirect to="/" />
            </Switch>
        </main>
    }
}

export default withRouter(CodeSearch)
